class FontAwesomeIcon(HtmlElement):
    """Font Awesome icon element."""
    # pylint: disable=R0903
    def __init__(
        self, icon_name, title=None, attributes=HtmlAttributes()
    ):
        attributes.update({
            'xmlns': 'http://www.w3.org/2000/svg',
            'viewBox': _FONT_AWESOME_MAP[icon_name]['view_box']
        })
        content = [
            '<!--! Font Awesome Free 6.4.2 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license/free (Icons: CC BY 4.0, Fonts: SIL OFL 1.1, Code: MIT License) Copyright 2023 Fonticons, Inc. -->'
        ]
        if title is not None:
            content.append(HtmlElement("title", title))
        content.append(_FONT_AWESOME_MAP[icon_name]["path"])
        super().__init__("svg", content, attributes)


class ShieldsIoIcon(HtmlElement):
    """Shields.io icon element."""
    # pylint: disable=R0903
    def __init__(
        self, icon_name, title=None, attributes=HtmlAttributes()
    ):
        attributes.update({
            'xmlns': 'http://www.w3.org/2000/svg',
            'preserveAspectRatio': 'xMidYMid meet'
        })
        content = [
            '<!--! Shields.io - https://shields.io License - https://github.com/badges/shields/blob/master/LICENSE (CC0 1.0 License) Copyright 2024 Shields.io -->'
        ]
        if title is not None:
            content.append(HtmlElement("title", title))
        content.append(_SHIELDS_IO_MAP[icon_name]["linearGradient"])
        content.append(_SHIELDS_IO_MAP[icon_name]["clipPath"])
        content.append(_SHIELDS_IO_MAP[icon_name]["g1"])
        content.append(_SHIELDS_IO_MAP[icon_name]["g2"])
        super().__init__("svg", content, attributes)
